    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
         
        
        Producto producto = new Producto();
        producto.setNombre(lblNombre.getText());
        producto.setCodigo(producto.generarCodigoUnico());
        producto.setPrecioUnitario(lblPrecio.getText());
        producto.setFechaIngreso(lblFecha.getText());
        producto.setFechaVencimiento(lblVencimiento.getText());
        producto.setStock(cboStock.getSelectedItem().toString());
        producto.setCategoria(cboCategoria.getSelectedItem().toString());
        
        ListaProductos.add(producto);
        
        java.awt.EventQueue.invokeLater(new Runnable() {
            public void run() {
                new FrmInventario().setVisible(true);
            }
        });
        
    }//GEN-LAST:event_btnRegistrarActionPerformed

